function startGame() {
    updatePage("start");
}

function updatePage(stage) {
    const storyElement = document.getElementById("story");
    const choicesElement = document.getElementById("choices");
    const imageElement = document.getElementById("image");

    if (!story[stage]) {
        console.error(`Stage ${stage} does not exist.`);
        return;
    }

    storyElement.textContent = story[stage].text;
    if (story[stage].image) {
        imageElement.innerHTML = `<img src="${story[stage].image}" alt="Story Image">`;
    } else {
        imageElement.innerHTML = '';
    }

    choicesElement.innerHTML = "";
    if (story[stage].choices.length > 0) {
        story[stage].choices.forEach(choice => {
            const button = document.createElement("button");
            button.textContent = choice.text;
            button.addEventListener("click", () => updatePage(choice.next));
            choicesElement.appendChild(button);
        });
    } else {
        endGame();
    }
}

function endGame() {
    const choicesElement = document.getElementById("choices");
    choicesElement.innerHTML = "";
    // Add any additional end game logic here, such as displaying a final message
}

document.addEventListener("DOMContentLoaded", startGame);